import React, { useState } from "react"
import './../css/chat.css'

function Profile(props) {

const[field, setField] = useState({
    firstName:'',
    lastName:'',
    email:'',
    profilePic:''

});
return(
    <div className="profile">
        <input 
        type="text"
        name="email"
        onChange={(e) => setEmail(e.target.value)}
        />
        <p> {field} </p>
    </div>
    );
}
export default Profile
