import React from 'react'
import './../../css/chat.css'
import { connect } from 'react-redux'
import { editMessage } from './../../action/convAction'

var editPenUrl = require('./../imgs/editpen.png')

class Box extends React.Component {
  constructor () {
    super()
    this.state = {
      editMode: false,
      selectedMsg: -1
    }
  }



  render () {
    return (
      <div className='box'>         
        {this.props.messageList.map((message, index) => {
          if (message.sender === 1) {
              return (
                <div key={index} className='sender'>
                  <span>{message.text}</span>
                  <img src={editPenUrl} alt='edit' className='editPen' title='edit' onClick={this.setState({ editMode: true, selectedMsg: index })} />
                </div>
              )
            } else {
              return (
                <input
                  key={index}
                  type='text'
                  value={message.text}
                  />
              )              
            }
          } 
        )}
      </div>
    )
  }
}
const mapStateToProps = (state) => ({
  messageList: state.messageList
})

const mapDispatchToProps = (dispatch) => ({
  dispatch: dispatch
})

export default connect(mapStateToProps, mapDispatchToProps)(Box)
