import React from 'react'
import { connect } from 'react-redux'
import { getUserName } from './../../action/convAction'
// import { openConversation } from './../../action/convAction'
import { getConversationId, getMessageList } from './../../action/convAction'
var profileUrl = require('./../imgs/avatar.png')

class Conversation extends React.Component {

  handleOpenConversation () {

    this.props.dispatch(getUserName(this.props.name, this.props.family))
    this.props.dispatch(getConversationId(this.props.conversations[this.props.item].id))

    // this.props.dispatch(openConversation())
    const fdata = new FormData()
    fdata.append('token', this.state.token)
    fdata.append('conversation_id', this.props.conversations[this.props.item].id)
    fdata.append('size', 10)
    fdata.append('date', (new Date().getTime() / 1000).toFixed(0))

    axios.post('https://api.paywith.click/conversation/details/', fdata) 

      .then((response) => {
        console.log('details::', response.data)
        this.props.dispatch((getMessageList(response.data.data.messages)))     
       })
      .catch(function (error) {
        console.log('detailsError::', error)
      })
  }




  handleClick () {

    this.props.onClick()
  }

  render () {
    return (
      <div className='conversation' onClick={() => this.handleClick()}>
        <img src={profileUrl} alt='profile' className='profileImg' />
        <div className='infoContainer'>
          <div className='info1'>
            <span className='name'>{this.props.name}</span>
            <span>id: {this.props.id}</span>
          </div>
          <div className='info2'>
            <span></span>
            {/* <span className='unseen'> {this.props.unseenMessage}</span> */}
          </div>
        </div>
      </div>
    )
  }
}

const mapDispatchToProps = (dispatch) => ({
  dispatch: dispatch
})

export default connect(mapDispatchToProps)(Conversation)
