import React, { useState } from "react"
import './../css/chat.css'

function Profile(props) {

const[field, setField] = useState({
    firstName:'name',
    lastName:'lastname',
    email:'',
    profilePic:''

});
return(
    <div className="profile">
        firstName:
        <input 
        type="text"
        name="email"
        onChange={(e) => setField(e.target.value)}
        />
        <p> {field.firstName} </p>
        lastName:
        <input
        type="text"
        name="lastName"
        onChange={(e) => setField(e.target.value)}
        />
        <p> {field.lastName} </p>
        <input
        type="text"
        name="email"
        onChange={(e) => setField(e.target.value)}
        />
        <p> {field.email} </p>
    </div>
    );
}
export default Profile
