import React from 'react'
import './../../css/chat.css'

class Box extends React.Component{
    constructor(props){
        super(props)

        this.state = {
            messageList: [
                {
                  text: 'salam',
                  date: '3/2',
                  sender: 1,
                  receiver: 3
                },
                {
                  text: 'salam',
                  date: '3/2',
                  sender: 3,
                  receiver: 1
                },
                {
                  text: 'khoobi?',
                  date: '3/2',
                  sender: 1,
                  receiver: 3
                },
                {
                  text: 'Are',
                  date: '3/2',
                  sender: 3,
                  receiver: 1
                }
              ],
        }

    }

    render() {
        console.log('prpssss:', this.state.messageList)
        return(
            <div>

        {
                this.props.messageList.map((message, index) => {
                    if (message.sender === 1) {
                    return (
                        <div key={index} className='sender' ><span>{message.text}</span></div>
                    )
                    } else {
                    return (
                        <div key={index} className='receiver' ><span>{message.text}</span></div>
                    )
                    }
                })
        }

            </div>
        )
    }
}
export default Box
