import React from 'react'
import './../../css/chat.css'
import Conversation from './../messenger/conversation'
import createNewConversation from './../../action/convAction'

class List extends React.Component {
    constructor () {
      super()

      this.state = {
        newConv: '',
        suggestionUsers: [],
        token: window.localStorage.getItem('token')
      }
    }

    handleClick () {
      this.state.dispatch(createNewConversation(this.state.newConv))
      this.setState({ newConv: '' })
    }

    render(){
        return(
            <div className="list">

             { this.props.conversationList.map((item, index) => (
              <Con
                key={index}
                name={item.firstName}
                lastName={item.lastName}
                latestMessage={item.latestMessage}
                unseenMessage={item.unseenMessage} />
            )
            )

        }
            </div>
        )
    }
}

export default List
