import React from 'react'
import './../../css/chat.css'
import { connect } from 'react-redux'

var editPenUrl = require('./../imgs/editpen.png')

class Box extends React.Component{
    constructor() {
        super()

        this.state = {
            editMode: false,
            selectedMsg: -1

        }
    }

    render() {
        console.log('prpssss:', this.props.messageList)
        return(
            <div className="box">

        {
                this.props.messageList.map((message, index) => {
                    if (message.sender === 1) {
                        if(!(this.state.editMode === true && this.state.selectedMsg === index)) {
                            return (
                                <div key={index} className='sender'>
                                    <span>{message.text} </span>
                                    <img src={editPenUrl} alt="edit" className="editPen" title="edit" />
                                </div>
                            )
                            } else {
                            return (
                                <div key={index} className='receiver'>
                                    <span>{message.text}</span>
                                </div>
                                )
                            }
                        }

                })
        }

            </div>
        )
    }
}
const mapStateToProps = (state) => ({
  messageList: state.messageList
})

export default connect(mapStateToProps)(Box)
